function showScore () {
    press_text = "presses"
    if (button_presses == 1) {
        press_text = "press"
    }
    game.splash("Level " + (current_level + 1), "" + button_presses + " " + press_text)
}
function Start() {
    resquest = false
    current_level = 0
    scene.setBackgroundImage(assets.image`blank`)
    levels = [
    tiles.createMap(tilemap`level8`),
    tiles.createMap(tilemap`level3`),
    tiles.createMap(tilemap`level4`),
    tiles.createMap(tilemap`level5`),
    tiles.createMap(tilemap`level6`),
    tiles.createMap(tilemap`level7`),
    tiles.createMap(tilemap`level8`)
    ]
    info.startCountup()
    info.setLife(3)
    initLevel(false)
    initPlayers()
}
